

--1
--CREATION
CREATE TABLE T7_BANK(
BANK_ID CHAR(3) constraint t7bks_id_pk primary key,
BANK_NAME VARCHAR2(100) constraint t7bks_name_nn not null,
SLA_TIME NUMBER(3) 
constraint t7s_time_nn not null
constraint t7s_time_min CHECK (sla_time>0),
BANK_CONTACT_NUMBER NUMBER(11) constraint t7bks_contact_number_nn not null,
BANK_MANAGER VARCHAR2(50) constraint t7bks_manager_nn not null
);

--INSERT



--2
--CREATE
CREATE TABLE T7_DEPARTMENT(
DEPARTMENT_ID CHAR(5) constraint t7dp_id_pk primary key,
DEPARTMENT_NAME VARCHAR2(100) constraint t7dp_name_nn not null,
DEPARTMENT_MANAGER VARCHAR2(50) constraint t7dp_manger_nn not null,
DP_CONTACT_NUMBER NUMBER(11) constraint t7dp_constact_number_nn not null,
DEPARTMENT_CATEGORY CHAR(10) constraint t7dp_category_nn not null
);

--INSERT

--3
--CREATE
CREATE TABLE T7_EMPLOYEE(
EMPLOYEE_ID NUMBER(10) constraint t7_emp_id_pk primary key,
DEPARTMENT_ID CHAR(5) constraint t7_dep_id_fk
references T7_DEPARTMENT(DEPARTMENT_ID),
EE_FIRST_NAME VARCHAR2(50) constraint t7_emp_Fname_nn not null,
EE_LAST_NAME VARCHAR2(50) constraint t7_emp_Lname_nn not null,
DATE_OF_HIRE DATE default sysdate constraint t7_emp_doh_nn not null,
SUPERVISOR_ID NUMBER(10) constraint t7_sup_id_fk
references T7_EMPLOYEE(EMPLOYEE_ID)
);

--INSERT


--4
--CREATE
CREATE TABLE T7_MERCHANT(
MERCHANT_ID CHAR(3) not null,
MERCHANT_ADD VARCHAR2(100) not null,
MERCHANT_NAME VARCHAR2(50) constraint t7_mn_nn not null,
CONTACT_NUMBER NUMBER(11) constraint t7_mc_cn_nn not null,
CONSTRAINT t7_merc_pk PRIMARY KEY (MERCHANT_ID,MERCHANT_ADD)
);

--INSERT





--5
--CREATE
CREATE TABLE T7_TERMINAL(
TERMINAL_ID char(3) constraint t7_ter_tid_pk primary key,
BANK_ID CHAR(3) constraint t7_ter_bk_id_fk 
references T7_BANK(BANK_ID),
MERCHANT_ID CHAR(3) constraint t7_ter_mid_nn not null,
MERCHANT_ADD VARCHAR2(100) constraint t7_ter_madd_nn not null,
CONTACT_PERSON VARCHAR2(100) constraint t7_ter_cp_nn not null,
CONSTRAINT t7_ter_mc_id_add_fk FOREIGN KEY (MERCHANT_ID, MERCHANT_ADD) REFERENCES T7_MERCHANT(MERCHANT_ID, MERCHANT_ADD)
);

--INSERT





--6
--CREATE
CREATE TABLE T7_WAREHOUSE(
WAREHOUSE_ID CHAR(5),
TERMINAL_PART_NO char(3),
QUANTITY NUMBER,
CONSTRAINT t7_wh_id_pk PRIMARY KEY (WAREHOUSE_ID,TERMINAL_PART_NO)
);

ALTER TABLE T7_WAREHOUSE
ADD CONSTRAINT t7_qty_positive check(quantity > -1);

--INSERT


--7
--CREATE
CREATE TABLE T7_STAGING_DEP(
DEPARTMENT_ID CHAR(5),
WAREHOUSE_ID CHAR(5),
TERMINAL_PART_NO char(3),
QUANTITY_IN_HAND NUMBER,
MINI_STOCK NUMBER,
MAX_STOCK NUMBER,
CONSTRAINT t7_sd_dep_fk FOREIGN KEY (DEPARTMENT_ID) REFERENCES T7_DEPARTMENT(DEPARTMENT_ID),
CONSTRAINT t7_sd_wh_fk FOREIGN KEY (WAREHOUSE_ID,TERMINAL_PART_NO) REFERENCES T7_WAREHOUSE(WAREHOUSE_ID,TERMINAL_PART_NO)
);

--INSERT



--8
--CREATE
CREATE TABLE T7_SERVICE_ORDER(
SERVICE_ORDER_ID CHAR(10) constraint t7_so_id_pk primary key,
BANK_ID CHAR(3) constraint t7_so_bk_id_pk 
references T7_BANK(BANK_ID),
TERMINAL_ID char(3) constraint t7_so_tid_fk 
references T7_TERMINAL(TERMINAL_ID),
SERVICE_ORDER_CREATION_TIME timestamp,
SERVICE_ORDER_RESOLUTION_TIME timestamp,
FLM_COMMENTS VARCHAR2(100),
CALL_CENTER_COMMENTS VARCHAR2(100),
ENGINEER_COMMENTS VARCHAR2(100),
SERVICE_ORDER_STATUS VARCHAR2(100) constraint t7_sOrder_sos_nn not null
);

--INSERT



--9
--CREATE
CREATE TABLE T7_SERVICE_ORDER_ASSIGNMENT(
SERVICE_ORDER_ID CHAR(10),
EMPLOYEE_ID NUMBER(10),
DEPARTMENT_ID CHAR(5),
CONSTRAINT t7_sa_id_pk PRIMARY KEY (SERVICE_ORDER_ID,EMPLOYEE_ID,DEPARTMENT_ID),
CONSTRAINT t7_sa_id_fk FOREIGN KEY (SERVICE_ORDER_ID) REFERENCES T7_SERVICE_ORDER (SERVICE_ORDER_ID),
CONSTRAINT t7_sa_emp_fk FOREIGN KEY (EMPLOYEE_ID) REFERENCES T7_EMPLOYEE (EMPLOYEE_ID),
CONSTRAINT t7_sa_dep_fk FOREIGN KEY (DEPARTMENT_ID) REFERENCES T7_DEPARTMENT(DEPARTMENT_ID)
);

--INSERT


